define constant $governors-car = make(<car>);

define method inspect-vehicle(car == $governors-car,
	i :: <state-inspector>) => ();
	wave-through(car);
end;

define library Vehicle-Application
  use Dylan;
  use My-GUI-Classes;
  use Vehicles;
end;

block ()
  1 + 1;
end; // returns 2

block (finished)
	if (sky-is-green())
		finished("Weird!");
	end;
	"All's well."
end block;

let fd = open-input-file();
block (return)
	let (errorcode, data) = read-data(fd);
	if (errorcode)
		return(errorcode);
	end if;
	process-data(data);
cleanup
	close(fd);
end;
